// 입력된 URL 배열 처리
const body = $input.first().json.body || $input.first().json;
const urls = body.urls || [];

if (urls.length === 0) {
  return [{ json: { error: 'No URLs provided', images: [] } }];
}

// 첫 번째 URL만 처리 (단순화)
const url = urls[0];

if (!url || !url.startsWith('http')) {
  return [{ json: { url: url, error: 'Invalid URL', images: [] } }];
}

return [{ json: { url: url } }];

// HTML에서 이미지 URL 추출
const html = $input.first().json.data || $input.first().json.body || '';

// URL 가져오기
let url = '';
try {
  url = $('URL 유효성 검증').first().json.url || '';
} catch(e) {
  url = 'https://www.lge.co.kr';
}

const images = new Set();

// og:image 우선 추출
const ogMatch = html.match(/<meta[^>]*property=["']og:image["'][^>]*content=["']([^"']+)["']/i);
if (ogMatch) {
  images.add(ogMatch[1]);
}

// LG 제품 이미지 패턴
const imgPatterns = [
  /["'](https?:\/\/[^"']*\.lge\.co\.kr[^"']*\.(jpg|jpeg|png|webp)[^"']*)["']/gi,
  /["'](https?:\/\/[^"']*\.lg\.com[^"']*\.(jpg|jpeg|png|webp)[^"']*)["']/gi,
  /["']([^"']*\/images\/[^"']+\.(jpg|jpeg|png|webp)[^"']*)["']/gi,
  /["'](https?:\/\/[^"']+\.(jpg|jpeg|png|webp))["']/gi
];

for (const pattern of imgPatterns) {
  let match;
  while ((match = pattern.exec(html)) !== null) {
    let imgUrl = match[1];
    if (!imgUrl.includes('icon') && !imgUrl.includes('logo') && !imgUrl.includes('btn') && !imgUrl.includes('sprite')) {
      if (imgUrl.startsWith('//')) {
        imgUrl = 'https:' + imgUrl;
      }
      images.add(imgUrl);
    }
  }
}

const imageArray = Array.from(images).slice(0, 5);

return [{
  json: {
    url: url,
    images: imageArray,
    count: imageArray.length,
    extractedAt: new Date().toISOString()
  }
}];
