// [LOG 1] 입력된 데이터 확인
const body = $input.first().json.body || $input.first().json;
const urls = body.urls || [];

console.log('=== [LOG 1] URL 유효성 검증 시작 ===');
console.log('받은 body:', JSON.stringify(body));
console.log('URLs 배열:', JSON.stringify(urls));
console.log('URL 개수:', urls.length);

if (urls.length === 0) {
  console.log('[ERROR] URL이 없습니다!');
  return [{ json: { error: 'No URLs provided', images: [], log: 'No URLs' } }];
}

// 각 URL을 개별 아이템으로 반환
const items = urls.map((url, idx) => {
  console.log(`[LOG 1-${idx+1}] URL ${idx+1}: ${url}`);
  return { json: { url: url, urlIndex: idx + 1, totalUrls: urls.length } };
});

console.log('[LOG 1] 반환할 아이템 수:', items.length);
return items;

// 이미지 URL 추출 - 개선된 버전
const items = $input.all();
const allImages = [];

for (let i = 0; i < items.length; i++) {
  const item = items[i];
  const html = item.json.data || item.json.body || '';
  const images = new Set();

  // 1. og:image 메타 태그
  const ogMatch = html.match(/<meta[^>]*property=["']og:image["'][^>]*content=["']([^"']+)["']/i);
  if (ogMatch) {
    images.add(ogMatch[1]);
  }

  // 2. LG 갤러리 이미지 패턴 (medium01, medium-interior01 등)
  const galleryPattern = /["'](https?:\/\/www\.lge\.co\.kr\/kr\/images\/[^"']*\/gallery\/[^"']+\.(jpg|jpeg|png|webp))["']/gi;
  let match;
  while ((match = galleryPattern.exec(html)) !== null) {
    images.add(match[1]);
  }

  // 3. LG 제품 이미지 일반 패턴
  const lgPatterns = [
    /["'](https?:\/\/www\.lge\.co\.kr[^"']*\.(jpg|jpeg|png|webp))["']/gi,
    /["'](https?:\/\/static\.lge\.co\.kr[^"']*\.(jpg|jpeg|png|webp))["']/gi,
    /["'](\/kr\/images\/[^"']+\.(jpg|jpeg|png|webp))["']/gi
  ];

  for (const pattern of lgPatterns) {
    while ((match = pattern.exec(html)) !== null) {
      let imgUrl = match[1];
      // 제외할 이미지 필터링
      if (imgUrl.includes('icon') || imgUrl.includes('logo') || imgUrl.includes('btn') || 
          imgUrl.includes('sprite') || imgUrl.includes('banner') || imgUrl.includes('common')) {
        continue;
      }
      // 상대 경로 처리
      if (imgUrl.startsWith('/kr/')) {
        imgUrl = 'https://www.lge.co.kr' + imgUrl;
      }
      images.add(imgUrl);
    }
  }

  // 각 URL에서 최대 5개씩 추출
  const imageArray = Array.from(images).slice(0, 5);
  allImages.push(...imageArray);
}

// 중복 제거 후 최대 10개
const uniqueImages = [...new Set(allImages)].slice(0, 10);

return [{
  json: {
    images: uniqueImages,
    count: uniqueImages.length,
    extractedAt: new Date().toISOString()
  }
}];
