// 입력된 URL 배열 처리
const urls = $input.first().json.body.urls || [];
const results = [];

for (const url of urls) {
  // URL 유효성 검증
  if (!url || !url.startsWith('http')) {
    results.push({
      url: url,
      error: 'Invalid URL',
      images: []
    });
    continue;
  }

  // LG 도메인 확인
  const isLGDomain = url.includes('lg.com') || url.includes('lge.com');

  results.push({
    url: url,
    isLGDomain: isLGDomain,
    needsJina: true  // 동적 렌더링 필요 여부
  });
}

return results.map(r => ({ json: r }));

// HTML에서 이미지 URL 추출
const html = $input.first().json.data || $input.first().json.body || '';
const url = $input.first().json.url || $('URL 유효성 검증').first().json.url;

// 이미지 URL 추출 패턴들
const patterns = [
  // og:image 메타 태그
  /<meta[^>]*property=["']og:image["'][^>]*content=["']([^"']+)["']/gi,
  // 일반 이미지 태그
  /<img[^>]*src=["']([^"']+)["'][^>]*>/gi,
  // data-src (lazy loading)
  /<img[^>]*data-src=["']([^"']+)["'][^>]*>/gi,
  // picture source
  /<source[^>]*srcset=["']([^"']+)["'][^>]*>/gi
];

const images = new Set();

// og:image 우선 추출
const ogMatch = html.match(/<meta[^>]*property=["']og:image["'][^>]*content=["']([^"']+)["']/i);
if (ogMatch) {
  images.add(ogMatch[1]);
}

// 제품 이미지 패턴 (LG 스펙 페이지 특화)
const productImagePatterns = [
  /["']([^"']*\.lg\.com[^"']*\/images\/[^"']+\.(jpg|jpeg|png|webp))["']/gi,
  /["']([^"']*product[^"']*\.(jpg|jpeg|png|webp))["']/gi,
  /["']([^"']*\/p\/[^"']+\.(jpg|jpeg|png|webp))["']/gi
];

for (const pattern of productImagePatterns) {
  let match;
  while ((match = pattern.exec(html)) !== null) {
    if (match[1] && !match[1].includes('icon') && !match[1].includes('logo')) {
      // 상대 경로를 절대 경로로 변환
      let imgUrl = match[1];
      if (imgUrl.startsWith('//')) {
        imgUrl = 'https:' + imgUrl;
      } else if (imgUrl.startsWith('/')) {
        const baseUrl = new URL(url);
        imgUrl = baseUrl.origin + imgUrl;
      }
      images.add(imgUrl);
    }
  }
}

// 큰 이미지 우선 정렬 (파일명에 크기 정보가 있는 경우)
const imageArray = Array.from(images).slice(0, 5);  // 최대 5개

return [{
  json: {
    url: url,
    images: imageArray,
    count: imageArray.length,
    extractedAt: new Date().toISOString()
  }
}];
